import React, { useEffect, useState } from 'react';
import logo from './logo.svg';
import './App.css';
import mergeImages from "merge-images";
import image1 from "./images/image-1.jpeg";
import image2 from "./images/image-2.jpeg";
import image3 from "./images/image-3.jpeg";
import image4 from "./images/image-4.jpeg";

function App() {

  const [src, setSrc] = useState<string>("");

  useEffect(() => {
    mergeImages([
      {
        src:
          "data:image/jpeg;base64,",
        x: 0,
        y: 0
      },
      {
        src:
          "data:image/jpeg;base64,",
        x: 50,
        y: 0
      },
      {
        src:
          "data:image/jpeg;base64,",
        x: 0,
        y: 50
      },
      {
        src:
          "data:image/jpeg;base64,",
        x: 50,
        y: 50
      }
    ])
      .then((src) => setSrc(src))
      .catch((err) => console.log(err));
  }, []);
  

  return (
    <div className="App">
      <h1>Original Images</h1>
      <ul>
        <li>
          <img src={image1} alt="" />
        </li>
        <li>
          <img src={image2} alt="" />
        </li>
        <li>
          <img src={image3} alt="" />
        </li>
        <li>
          <img src={image4} alt="" />
        </li>
      </ul>
      <h1>Combined Image</h1>
      {src && <img src={src} alt="" />}
      {/* <img src={src} id="combined-image" alt="" /> */}
    </div>
  );

  // return (
  //   <div className="App">
  //     <header className="App-header">
  //       <img src={logo} className="App-logo" alt="logo" />
  //       <p>
  //         Edit <code>src/App.tsx</code> and save to reload.
  //       </p>
  //       <a
  //         className="App-link"
  //         href="https://reactjs.org"
  //         target="_blank"
  //         rel="noopener noreferrer"
  //       >
  //         Learn React
  //       </a>
  //     </header>
  //   </div>
  // );
}

export default App;
